INSERT INTO `faq` VALUES (14,'vi','<p class=\"MsoListParagraph\" style=\"margin-left:0cm;mso-add-space:auto;text-align:\r\njustify;text-indent:0cm;mso-list:l0 level1 lfo1\"><span style=\"font-size:13.0pt;line-height:107%;\r\nfont-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;\">Tôi\r\ncó thể hủy đơn hàng của mình sau khi thanh toán được không?<o:p></o:p></span></p>','<p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-size:13.0pt;\r\nline-height:107%;font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;\">Về\r\ncơ bản, bạn có thể hủy đơn hàng của mình sau khi thanh toán nếu chúng tôi kịp\r\nthông báo lại cho người nhận mua hàng và họ chưa mua hàng. Tuy nhiên bạn sẽ\r\nkhông được bồi hoàn phí dịch vụ và có thể sẽ nhận đánh giá tiêu cực từ Người\r\nmua hộ.<o:p></o:p></span></p><p>\r\n\r\n</p><p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-size:13.0pt;\r\nline-height:107%;font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;\">Trong\r\ntrường hợp người nhận mua hàng đã mua sản phẩm, đơn hàng sẽ không được phép hủy\r\ndưới mọi trường hợp.<o:p></o:p></span></p>',1,1,1),(15,'vi','<p class=\"MsoListParagraph\" style=\"margin-left:0cm;mso-add-space:auto;text-align:\r\njustify;text-indent:0cm;mso-list:l0 level1 lfo1\"><span style=\"font-size:13.0pt;line-height:107%;\r\nfont-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;\">Người\r\nnhận mua hàng không giao sản phẩm đúng như yêu cầu ban đầu hoặc sản phẩm bị hư\r\nhỏng nặng, tôi phải làm gì?<o:p></o:p></span></p>','<p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-size:13.0pt;\r\nline-height:107%;font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;\">Trong\r\ntrường hợp có vấn đề liên quan đến sản phẩm giữa người mua và người nhận mua\r\nhàng, SubSuf sẽ là trọng tài đứng ra phân xử, nếu chúng tôi kết luận lỗi không\r\nthuộc về Người mua hàng ( Shopper) thì Shopper sẽ được quyền từ chối nhận sản\r\nphẩm và bồi hoàn tiền đầy đủ.<o:p></o:p></span></p>',1,2,1),(16,'vi','<p class=\"MsoListParagraph\" style=\"margin-left:0cm;mso-add-space:auto;text-align:\r\njustify;text-indent:0cm;mso-list:l0 level1 lfo1\"><span style=\"font-size:13.0pt;line-height:107%;\r\nfont-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;\">Tôi\r\nphải làm gì vào lúc nhận hàng?<o:p></o:p></span></p>','<p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-size:13.0pt;\r\nline-height:107%;font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;\">Khi\r\nnhận hàng, bạn sẽ xác nhận đã nhận được hàng trên hệ thống để người nhận mua\r\nhàng có thể được thanh toán đầy đủ. Nếu người mua hàng không xác nhận mặc dù đã\r\nnhận được hàng, sau 14 ngày kể từ ngày hai bên đặt lịch giao nhận hàng, và người\r\nmua hàng không có ý kiến phản hồi, chúng tôi mặc định là đơn hàng đã được giao\r\nthành công.<o:p></o:p></span></p><p>\r\n\r\n</p><p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-size:13.0pt;\r\nline-height:107%;font-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;\">Bạn\r\ncũng nên đánh giá, nhận xét người nhận mua hộ để lấy cơ sở tham khảo cho những\r\nngười mua hàng sau tiếp đó.<o:p></o:p></span></p>',1,3,1),(17,'vi','<p class=\"MsoListParagraph\" style=\"margin-left:0cm;mso-add-space:auto;text-align:\r\njustify;text-indent:0cm;mso-list:l0 level1 lfo1\"><span style=\"font-size:13.0pt;line-height:107%;\r\nfont-family:&quot;Verdana&quot;,&quot;sans-serif&quot;;mso-bidi-font-family:&quot;Times New Roman&quot;\">Mua\r\nhàng qua SubSuf có đảm bảo không, nếu người mua hộ không giao hàng thì sao ?<o:p></o:p></span></p>','<p class=\"MsoNormal\